import React, {Component} from 'react'
import {Button , Card , Container, Row, Col} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import styles from './users.css';


class Users extends Component {  
    constructor(){
        super();
        this.state = {
            user:[],
            users: [],
            projects: [],
            areas: [],
        }
    }

    componentDidMount(){
        const urls = [
            'http://localhost:3000/users/logged',
            'http://localhost:3000/users',
            'http://localhost:3000/users/projects',
            'http://localhost:3000/areas/logged'

        ]
        let requests = urls.map((url) => fetch(url));
        Promise.all(requests)
            .then((responses) => {
                return Promise.all(responses.map((response) => response.json()));
            })
            .then((responses) => {
                this.setState({
                    user : responses[0],
                    users: responses[1],
                    projects: responses[2],
                    areas: responses[3]
                });
            });

    }



    render (){
        return (

                <Container fluid style={{ fontcolor: 'black' }}>
                    <Row >
                        <Col className="mh-100" md lg="2">
                            <Card >
                                <Card.Img variant="top" src={require('./../images/user/rafael_oliveira.jpg')} />
                                <Card.Body className="mh-100">

                                    <Card.Title>
                                        {this.state.user.map(user =>
                                            <>
                                            <p>{user.first_name} {user.last_name}</p>
                                            <p>{user.degree} {user.major}</p>
                                            </>      
                                        )}
                                    </Card.Title>
                                    <Card.Text>

                                        {this.state.areas.map(areas =>
                                        <li key={areas.id}>{areas.name}</li>    
                                    )}
                                    </Card.Text>

                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                    <Row>
                        <Col >

                            <h2>Users</h2>
                                <ul>
                                    {this.state.users.map(users =>
                                        <li key={users.id}>{users.first_name} {users.last_name}</li>    
                                    )}
                                </ul>
                                    {this.state.projects.map(projects =>
                                        <li key={projects.name}>{projects.name} {projects.description}</li>    
                                    )}
                                <ul>

                            </ul>

                        </Col>
                    </Row>
                </Container>




        );
    }  
}

export default Users;
