import React, {Component} from 'react'
import {Button , Card , Container, Row, Col} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css'
import styles from './users.css';


class Users extends Component {  
    constructor(){
        super();
        this.state = {
            user:[],
            users: [],
            projects: []
        }
    }

    componentDidMount(){
        const urls = [
            'http://localhost:3000/users/logged',
            'http://localhost:3000/users',
            'http://localhost:3000/users/projects'

        ]
        let requests = urls.map((url) => fetch(url));
        Promise.all(requests)
            .then((responses) => {
                return Promise.all(responses.map((response) => response.json()));
            })
            .then((responses) => {
                this.setState({
                    user : responses[0],
                    users: responses[1],
                    projects: responses[2],
                });
            });

    }



    render (){
        return (

                <Container fluid style={{ fontcolor: 'black' }}>
                    <Row id="test" tyle={{ height: '10rem' }}>
                        <Col md lg="3">
                            <Card >
                                <Card.Img variant="top" src="../../../public/users/rafael_oliveira.jpg" />
                                <Card.Body>
                                    <Card.Title>Card Title</Card.Title>
                                    <Card.Text>
                                        {this.state.user.map(user =>
                                            <span key={user.id}>{user.first_name} {user.last_name}</span>    
                                        )}
                                    </Card.Text>

                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                    <Row>
                        <Col >

                            <h2>Users</h2>
                                <ul>
                                    {this.state.users.map(users =>
                                        <li key={users.id}>{users.first_name} {users.last_name}</li>    
                                    )}
                                </ul>
                                    {this.state.projects.map(projects =>
                                        <li key={projects.name}>{projects.name} {projects.description}</li>    
                                    )}
                                <ul>

                            </ul>

                        </Col>
                    </Row>
                </Container>




        );
    }  
}

export default Users;
